export default (state, action) => {
  switch (action.type) {
    case "ADD_MOVIE_TO_Queue":
      return {
        ...state,
        Queue: [action.payload, ...state.Queue],
      };
    case "REMOVE_MOVIE_FROM_Queue":
      return {
        ...state,
        Queue: state.Queue.filter(
          (movie) => movie.id !== action.payload
        ),
      };
    case "ADD_MOVIE_TO_AlreadySeen":
      return {
        ...state,
        Queue: state.Queue.filter(
          (movie) => movie.id !== action.payload.id
        ),
        AlreadySeen: [action.payload, ...state.AlreadySeen],
      };
    case "MOVE_TO_Queue":
      return {
        ...state,
        AlreadySeen: state.AlreadySeen.filter(
          (movie) => movie.id !== action.payload.id
        ),
        Queue: [action.payload, ...state.Queue],
      };
    case "REMOVE_FROM_AlreadySeen":
      return {
        ...state,
        AlreadySeen: state.AlreadySeen.filter((movie) => movie.id !== action.payload),
      };
    default:
      return state;
  }
};
